// チェッカー
function setup() {
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      Reflect(i * 8,j * 8,size,size)
      // BLANK[1] ヒント： rectのx座標は size * i, y座標は size * j
    }
  }
}

// チェッカー
function setup() {
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      if ((i + j) % 2 == 0) {
        fill(255); // 白
      } else {
        fill(200); // 灰色
      }
      rect(i * size, j * size, size, size)
      // BLANK[1] ヒント： rectのx座標は size * i, y座標は size * j
    }
  }
}

// チェッカー
function setup() {
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      if ((i + j) % 2 == 0) {
        fill(255); // 白
      } else {
        fill(200); // 灰色
      }
      rect(i * size, j * size, size, size)
      
      if ((i + j) % 2 == 1) {
        fill(255, 0, 0); // 赤
        circle(size * i + size / 2, size * j + size / 2, size * 0.6); // 中心に丸
      }
    }
  }
}

