function drawGround(){
  fill(64, 192, 64);
  rect(0, groundY, width, height - groundY);
}

function controlMove(){
  let speed = 4;

  if (keyIsDown(SHIFT)) speed = 8;

  if (keyIsDown(LEFT_ARROW))  x -= speed;
  if (keyIsDown(RIGHT_ARROW)) x += speed;

  // ジャンプ（上矢印）
  if (keyIsDown(UP_ARROW) && y >= groundY) {
    vy = -15;
  }
}

function applyGravity(){
  // 重力
  vy += g;
  y += vy;

  // 地面の上で停止
  if (y > groundY){
    y = groundY;
    vy = 0;
  }
}
